import React from 'react';
import { View, StyleSheet, Platform, KeyboardAvoidingView, Keyboard,ScrollView } from 'react-native';
import { Image } from 'react-native-expo-image-cache';
import AppText from '../Components/Text';


import colors from '../config/colors';
import ListItem from '../Components/lists/ListItem';
import ContactSellerForm from '../Components/ContactSellerFrom';

function ListingDetailsScreen({ route }) {
       const listing = route.params;
       return (
              <ScrollView>
              <KeyboardAvoidingView behavior="position" keyboardVerticalOffset={Platform.OS === "ios" ? 0 : 100} >
                     <Image
                            style={styles.image}
                            tint="light"
                            preview={{ uri: listing.images[0].thumbnailUrl }}
                            uri={listing.images[0].url} />
                     <View>
                            <View style={styles.detailsContainer}>
                                   <AppText style={styles.title} >{listing.title}</AppText>
                                   <AppText style={styles.price} >${listing.price}</AppText>
                                   <View style={styles.userContainer}>
                                          <ListItem title="Mosh Hamedani"
                                                 subTitle="5 Listings"
                                                 image={require("../assets/images/mosh.jpg")} />
                                   </View>

                            </View>
                            <ContactSellerForm listing={listing}/>

                     </View>
              </KeyboardAvoidingView>
              </ScrollView>
       );
}

const styles = StyleSheet.create({
       image: {
              width: "100%",
              height: 300
       },
       detailsContainer: {
              padding: 20
       },
       title: {
              fontSize: 24,
              fontWeight: "500"
       },
       price: {
              color: colors.secondary,
              fontSize: 20,
              fontWeight: "bold",
              marginVertical: 10
       },
       userContainer: {
              marginVertical: 40
       }

})

export default ListingDetailsScreen;
