import {
       StyleSheet, Text, Image, TouchableHighlight, View, ImageBackground,
       Alert, StatusBar, Dimensions,
       SafeAreaView, Button, Platform
} from 'react-native';
import {
       useDimensions, useDeviceOrientation
} from '@react-native-community/hooks';

export default function WelcomeScreen() {
       const { landscape } = useDeviceOrientation();
       return (
              <View style={styles.container}>
                     <ImageBackground source={require("./assets/background.jpg")} style={styles.image}>
                            <Text style={styles.subText}>Tools two go</Text>
                     </ImageBackground>
                     <Image
                            style={styles.tinyLogo}
                            source={require("./assets/logo-red.png")}
                     />
                     <View
                            style={{ 
                                   backgroundColor: "#fc5c65",
                                   width: "100%",
                                   height: 70,
                                   bottom: 70,
                                   position: "absolute"
                            }}

                     />
                     <View
                            style={{
                                   backgroundColor: "#4ecdc4",
                                   width: "100%",
                                   height: 70,
                                   bottom: 0,
                                   position: "absolute"
                                   
                            }}
                     />


                     
              </View>
       );
}

const styles = StyleSheet.create({
       container: {
              flex: 1,
       },
       image: {
              flex: 1,
              justifyContent: "center"

       },
       text: {
              color: "white",
              fontSize: 42,
              lineHeight: 84,
              fontWeight: "bold",
              textAlign: "center",
              backgroundColor: "#000000c0"
       },
       tinyLogo: {
              width: 100,
              height: 100,
              top: 100,
              left: 170,
              position: "absolute",
              fontSize: 50
       },
       subText: {
              color: "black",
              fontSize: 20,
              lineHeight: 84,
              fontWeight: "bold",
              textAlign: "center",
              top: 180,
              left: 150,
              position: "absolute",


       }

});
