import React from 'react';
import { Text, StyleSheet, Platform } from 'react-native'
function AppText(props) {
       return (
              <Text style={styles.text}>{props.children}</Text>
       );
}

const styles = StyleSheet.create({
       text: {
              color: "tomato",
              ...Platform.select({
                     ios: {
                            fontSize: 20,
                            fontFamily: "Avenir"
                     },
              })

          }

});

export default AppText;
